typedef struct {
    int         id;
    const char *name;
} IdName;

static inline const char *
lookupOrHex(int id, const IdName *table, char *buf, size_t buf_len) {
    for (; table->name != NULL; ++table) {
        if (table->id == id) {
            return table->name;
        }
    }
    snprintf(buf, buf_len, "0x%X", id);
    return buf;
}

